import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Button, TextField, Typography, Container, Grid, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper } from '@mui/material';
import axios from 'axios';
import './ScanningScreen.css'; // Import the stylesheet

interface MatchData {
  match: string;
  blueAlliance: number[];
  redAlliance: number[];
}

const ScanningScreen: React.FC = () => {
  const { district, matchNumber } = useParams<{ district: string; matchNumber: string }>();
  const [matchData, setMatchData] = useState<MatchData | null>(null);
  const [scannedTeams, setScannedTeams] = useState<number[]>([]);
  const [inputValue, setInputValue] = useState<string>('');
  const [responseMessage, setResponseMessage] = useState<string>('');

  useEffect(() => {
    const fetchMatchData = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/matches/${district}/${matchNumber}`);
        const responseData = response.data;

        setMatchData({
          match: matchNumber || '',
          blueAlliance: responseData.body.slice(0, 3),
          redAlliance: responseData.body.slice(3),
        });
      } catch (error) {
        console.error('Error fetching match data:', error);
      }
    };

    fetchMatchData();
  }, [district, matchNumber]);

  const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setInputValue(event.target.value);
  };

  const handleAddTeam = async () => {
    const teamData = `"${inputValue}"`;
    const teamNumberString = inputValue.split(',')[1].substring(1);
    const dataMatchNumber = parseInt(inputValue.split(',')[0].substring(0));
    if(isNaN(dataMatchNumber) || dataMatchNumber !== parseInt(matchNumber || '0')){
        setResponseMessage(`Invalid match number. Match number is ${matchNumber}`);
    }
    console.info(teamNumberString);
    if (isNaN(parseInt(teamNumberString))) {
      setResponseMessage('Invalid team data format. Please enter a valid input.');
    }

    const teamNumber = parseInt(teamNumberString);
    console.info(teamNumber);
    if (isNaN(teamNumber)) {
      setResponseMessage('Invalid team number. Please enter a valid number.');
    }

    try {
      const response = await axios.post(`http://localhost:8080/impi/${district}/add`, teamData, {
        headers: {
          'Content-Type': 'text/plain',
        },
      });

      if (matchData?.blueAlliance.includes(teamNumber) || matchData?.redAlliance.includes(teamNumber)) {
        setScannedTeams([...scannedTeams, teamNumber]);
      }
      setResponseMessage(response.data);
      setInputValue('');
    } catch (error) {
      console.error('Error adding team:', error);
      setResponseMessage('Error adding team');
    }
  };

  const handleKeyPress = (event: React.KeyboardEvent<HTMLInputElement>) => {
    if (event.key === 'Enter') {
      handleAddTeam();
    }
  };

  const isTeamScanned = (teamNumber: number) => {
    return scannedTeams.includes(teamNumber);
  };

  const handleNextMatch = () => {
    const nextMatchNumber = parseInt(matchNumber || '0') + 1;
    window.location.href = `/ScanningScreen/${district}/${nextMatchNumber}`;
  };

  return (
    <Container>
      <Typography variant="h3" align="center" gutterBottom className="title">
        Qualification Match {matchNumber}
      </Typography>
      {matchData ? (
        <Grid container justifyContent="center" spacing={4} className="grid-container">
          <Grid item xs={12} md={6}>
            <Typography variant="h5" align="center" gutterBottom>
              Blue Alliance
            </Typography>
            <TableContainer component={Paper} className="table">
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell align="center">Team Number</TableCell>
                    <TableCell align="center">Status</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {matchData.blueAlliance.map((teamNumber, index) => (
                    <TableRow key={index}>
                      <TableCell align="center" className="table-cell">
                        {teamNumber}
                      </TableCell>
                      <TableCell align="center" style={{ color: isTeamScanned(teamNumber) ? '#28a745' : '#dc3545' }} className="table-cell">
                        {isTeamScanned(teamNumber) ? 'Scanned' : 'Not Scanned'}
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
          </Grid>
          <Grid item xs={12} md={6}>
            <Typography variant="h5" align="center" gutterBottom>
              Red Alliance
            </Typography>
            <TableContainer component={Paper} className="table">
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell align="center">Team Number</TableCell>
                    <TableCell align="center">Status</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {matchData.redAlliance.map((teamNumber, index) => (
                    <TableRow key={index}>
                      <TableCell align="center" className="table-cell">
                        {teamNumber}
                      </TableCell>
                      <TableCell align="center" style={{ color: isTeamScanned(teamNumber) ? '#28a745' : '#dc3545' }} className="table-cell">
                        {isTeamScanned(teamNumber) ? 'Scanned' : 'Not Scanned'}
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
          </Grid>
        </Grid>
      ) : (
        <Typography variant="body1" align="center">
          Loading...
        </Typography>
      )}
      <Grid container justifyContent="center" spacing={2}>
        <Grid item>
          <TextField
            type="text"
            variant="outlined"
            value={inputValue}
            onChange={handleInputChange}
            onKeyPress={handleKeyPress}
            placeholder="Enter team data..."
            className="text-field"
            InputProps={{ classes: { input: 'input-text' } }}
          />
        </Grid>
        <Grid item>
          <Button variant="contained" onClick={handleAddTeam} className="add-team-button">
            Add Team
          </Button>
        </Grid>
      </Grid>
      <div className="response-message">
        <Typography variant="body1" align="center" style={{ color: responseMessage.includes('Error') ? '#dc3545' : '#28a745' }}>
          {responseMessage}
        </Typography>
      </div>
      <div className="next-match-button">
        <Button variant="contained" onClick={handleNextMatch}>
          Next Match
        </Button>
      </div>
    </Container>
  );
};

export default ScanningScreen;
