import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import { ChartData, ChartOptions } from 'chart.js';

interface TeamData {
  teamNumber: number;
  avgNotesFed: number;
}

const DashboardScreen: React.FC = () => {
  const [teamData, setTeamData] = useState<TeamData[]>([]);
  const [selectedMetric, setSelectedMetric] = useState<string>('avgNotesScoredInTeleop');
  const [loading, setLoading] = useState<boolean>(true);
  const [compCode, setCompCode] = useState<string>('2024mimus'); // Set a default compCode

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/impi/getAvgNotesFed/${compCode}`);
        setTeamData(response.data);
      } catch (error) {
        console.error('Error fetching team data:', error);
      } finally {
        setLoading(true);
      }
    };
    fetchData();
  }, [compCode]);

  const handleMetricChange = (event: React.ChangeEvent<HTMLSelectElement>) => {
    setSelectedMetric(event.target.value);
  };

  const handleCompCodeChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setCompCode(event.target.value);
  };

  const chartData: ChartData<'bar'> = {
    labels: teamData.map(team => `Team ${team.teamNumber}`),
    datasets: [
      {
        label: selectedMetric,
        data: teamData.map(team => team[selectedMetric as keyof TeamData]),
        backgroundColor: 'rgba(75,192,192,0.6)',
        borderColor: 'rgba(75,192,192,1)',
        borderWidth: 1,
      },
    ],
  };

  const chartOptions: ChartOptions<'bar'> = {
    responsive: true,
    scales: {
      y: {
        beginAtZero: true,
      },
    },
  };

  return (
    <div>
      <h1>Team Performance Dashboard</h1>

      <label htmlFor="compCode-input">Competition Code:</label>
      <input
        id="compCode-input"
        type="text"
        value={compCode}
        onChange={handleCompCodeChange}
      />

      <label htmlFor="metric-select">Select Metric:</label>
      <select id="metric-select" value={selectedMetric} onChange={handleMetricChange}>
        <option value="avgNotesFed">Avg Notes Fed</option>
      </select> 

     {loading ? (
        <p>Loading data...</p>
      ) : (
        <div>
          <Bar data={chartData} options={chartOptions} />

          <h2>Team Rankings</h2>
          <table>
            <thead>
              <tr>
                <th>Team Number</th>
                <th>Avg Notes Fed</th>
              </tr>
            </thead>
            <tbody>
              {teamData
                .sort((a, b) => b[selectedMetric as keyof TeamData] - a[selectedMetric as keyof TeamData])
                .map((team) => (
                  <tr key={team.teamNumber}>
                    <td>{team.teamNumber}</td>
                    <td>{team.avgNotesFed}</td>
                  </tr>
                ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default DashboardScreen;
