import * as React from 'react';
import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import Box from '@mui/material/Box';
import './App.css';
import Typography from '@mui/material/Typography';

import HomeScreen from './Components/HomeScreen/HomeScreen';
import MatchesScreen from './Components/MatchesScreen/MatchesScreen';
import { Route, BrowserRouter as Router, Routes, useLocation, useNavigate } from 'react-router-dom';
import ScanningScreen from './Components/ScanningScreen/ScanningScreen';
import RemoveDataScreen from './Components/RemoveDataScreen/RemoveDataScreen';

const NavigationTabs: React.FC = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [value, setValue] = React.useState(() => {
    switch (location.pathname) {
      case '/HomeScreen':
        return 0;
      case '/MatchesScreen':
      case '/MatchesScreen/:district':
        return 1;
      case '/RemoveDataScreen':
        return 2;
      default:
        return false; // Indicates no tab should be selected
    }
  });

  const handleChange = (event: React.SyntheticEvent, newValue: number) => {
    setValue(newValue);
    switch (newValue) {
      case 0:
        navigate('/HomeScreen');
        break;
      case 1:
        navigate('/MatchesScreen');
        break;
      case 2:
        navigate('/RemoveDataScreen');
        break;
      default:
        navigate('/HomeScreen');
    }
  };

  React.useEffect(() => {
    switch (location.pathname) {
      case '/HomeScreen':
        setValue(0);
        break;
      case '/MatchesScreen':
      case location.pathname.startsWith('/MatchesScreen/'):
        setValue(1);
        break;
      case '/RemoveDataScreen':
        setValue(2);
        break;
      default:
        setValue(false); // Indicates no tab should be selected
        break;
    }
  }, [location.pathname]);

  return (
    <Box sx={{ width: '100%', display: 'flex', justifyContent: 'flex-end', backgroundColor: 'midnightblue' }}>
      <Typography variant="h5" sx={{ flexGrow: 1, paddingTop: '1%', paddingLeft: '2%', color: 'chartreuse' }}>
        Impi Scouting Utility
      </Typography>
      <Tabs
        onChange={handleChange}
        value={value}
        aria-label="Tabs where selection follows focus"
        selectionFollowsFocus
        sx={{
          '& .MuiTabs-indicator': {
            backgroundColor: 'chartreuse',
          },
          '& .MuiTab-root.Mui-selected': {
            color: 'chartreuse',
          },
        }}
      >
        <Tab label="Home" style={{ color: 'chartreuse' }} />
        <Tab label="Matches" style={{ color: 'chartreuse' }} />
        <Tab label="Remove Data Screen" style={{ color: 'chartreuse' }} />
      </Tabs>
    </Box>
  );
};

const App: React.FC = () => {
  return (
    <Router>
      <NavigationTabs />
      <Routes>
        <Route path="/HomeScreen" element={<HomeScreen />} />
        <Route path="/MatchesScreen" element={<MatchesScreen />} />
        <Route path="/MatchesScreen/:district" element={<MatchesScreen />} />
        <Route path="/ScanningScreen/:district/:matchNumber" element={<ScanningScreen />} />
        <Route path="/RemoveDataScreen" element={<RemoveDataScreen />} />
        <Route path="*" element={<HomeScreen />} />
      </Routes>
    </Router>
  );
};

export default App;
