import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import { ChartData, ChartOptions } from 'chart.js';

interface TeamData {
  teamNumber: string; // Assuming teamNumber is a string
  avgNotesFed: number;
}

const DashboardScreen: React.FC = () => {
  const [teamData, setTeamData] = useState<TeamData[]>([]);
  const [loading, setLoading] = useState<boolean>(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:8080/impi/getAvgNotesFed');
        const formattedData = Object.entries(response.data).map(([teamNumber, avgNotesFed]) => ({
          teamNumber,
          avgNotesFed: typeof avgNotesFed === 'string' ? parseFloat(avgNotesFed) : avgNotesFed,
        }));
        setTeamData(formattedData);
      } catch (error) {
        console.error('Error fetching team data:', error);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  const chartData: ChartData<'bar'> = {
    labels: loading ? [] : teamData.map(team => `Team ${team.teamNumber}`),
    datasets: [
      {
        label: 'Avg Notes Fed',
        data: loading ? [] : teamData.map(team => team.avgNotesFed),
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
        borderColor: 'rgba(75, 192, 192, 1)',
        borderWidth: 1,
      },
    ],
  };

  const chartOptions: ChartOptions<'bar'> = {
    responsive: true,
    scales: {
      y: {
        beginAtZero: true,
      },
    },
  };

  return (
    <div>
      <h1>Team Performance Dashboard</h1>

      {loading ? (
        <p>Loading data...</p>
      ) : (
        <>
          <Bar data={chartData} options={chartOptions} />

          <h2>Team Rankings (Avg Notes Fed)</h2>
          <table>
            <thead>
              <tr>
                <th>Team Number</th>
                <th>Avg Notes Fed</th>
              </tr>
            </thead>
            <tbody>
              {teamData.sort((a, b) => b.avgNotesFed - a.avgNotesFed).map((team) => (
                <tr key={team.teamNumber}>
                  <td>{team.teamNumber}</td>
                  <td>{team.avgNotesFed}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}
    </div>
  );
};

export default DashboardScreen;
