import React, { useState, useEffect } from 'react';
import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Button,
} from '@mui/material';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { Link } from 'react-router-dom';
import './MatchesScreen.css'; // Import the stylesheet

interface MatchData {
  match: string;
  redalliance: string[];
  bluealliance: string[];
  numberOfTeamsScanned: number; // New field for number of teams scanned
}

const MatchesScreen: React.FC = () => {
  const { district } = useParams();
  const [matchData, setMatchData] = useState<MatchData[]>([]);

  useEffect(() => {
    const fetchMatchData = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/matches/${district}`);
        const responseData = response.data.body;

        const transformedData: MatchData[] = await Promise.all(
          Object.keys(responseData).map(async (matchNumber) => {
            const matchDataResponse = await axios.get(`http://localhost:8080/impi/getNumberScanned/${district}/${matchNumber}`);
            const numberOfTeamsScanned = matchDataResponse.data;

            return {
              match: matchNumber,
              redalliance: responseData[matchNumber].slice(3).map((team: number) => `${team}`),
              bluealliance: responseData[matchNumber].slice(0, 3).map((team: number) => `${team}`),
              numberOfTeamsScanned,
            };
          })
        );

        setMatchData(transformedData);
      } catch (error) {
        console.error('Error fetching match data:', error);
      }
    };

    fetchMatchData();
  }, [district]);

  return (
    <TableContainer component={Paper} className="table-container">
      <Table aria-label="match table" className="match-table">
        <TableHead>
          <TableRow className="match-table-head">
            <TableCell className="match-table-cell">Match</TableCell>
            <TableCell className="match-table-cell">Red Alliance</TableCell>
            <TableCell className="match-table-cell">Blue Alliance</TableCell>
            <TableCell className="match-table-cell">Scan</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {matchData.map((row, index) => (
            <TableRow
              key={index}
              className={`match-table-row ${row.numberOfTeamsScanned >= 6 ? 'highlighted-row' : ''}`}
            >
              <TableCell className="match-table-cell">{row.match}</TableCell>
              <TableCell className="red-alliance">{row.redalliance.join(', ')}</TableCell>
              <TableCell className="blue-alliance">{row.bluealliance.join(', ')}</TableCell>
              <TableCell className="match-table-cell">
                <Link to={`/ScanningScreen/${district}/${row.match}`} className="scan-button">
                  <Button variant="contained" color="primary">Scan</Button>
                </Link>
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
};

export default MatchesScreen;
