import React, { useState } from 'react';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemText from '@mui/material/ListItemText';
import { useNavigate } from 'react-router-dom';
import MatchesScreen from '../MatchesScreen/MatchesScreen';
import './styles.css'; // Import the stylesheet

const HomeScreen: React.FC = () => {
  const navigate = useNavigate();
  const [showTable, setShowTable] = useState(false);

  const handleButtonClick = (district: string) => {
    navigate(`/MatchesScreen/${district}`);
    console.info(`${district}`);
  };

  return (
    <Box className="container">
      <Typography variant="h3" className="title" gutterBottom>
        Welcome to Impi Scouting Util
      </Typography>
      <List className="list-container">
        {["2024mimil", "2024mimus", "2024micmp4", "2024arc"].map((value) => (
          <ListItem key={value} className="list-item" disableGutters>
            <ListItemText
              primary={
                <Typography variant="body1" className="list-text">
                  {value}
                </Typography>
              }
            />
            <Button 
              className="action-button" 
              variant="contained" 
              color="primary"
              onClick={() => handleButtonClick(value)}
            >
              Matches
            </Button>
          </ListItem>
        ))}
      </List>
    </Box>
  );
};

export default HomeScreen;
